import React, {useState} from 'react';
import { Form } from 'react-bootstrap';
import axios from 'axios';
import Spinner from '../Spinner/Spinner';


const Cono = () => {

    const background = {
        backgroundColor: "royalblue"
    }

    const text = {
        textAlign:"center",
        color:"snow"
    }

    const labelPosition = {
        color:"snow",
        marginLeft:"25%"
    }

    const selectPosition = {
        width:"50%",
        marginLeft:"25%"
    }

    const buttonStyle = {
        backgroundColor: "snow",
        border: "none",
        color: "royalblue",
        padding: "15px 32px",
        textAlign: "center",
        textDecoration: "none",
        display: "inline-block",
        fontSize: "16px",
        cursor: "pointer",
        marginLeft:"47%"
    }

    const sendOrder = (e) =>{
        e.preventDefault();

        const url = "http://localhost:8080/venta/crearVenta";

        axios.post(url,{
            tipoHelado:'Cono'
        })
        .then(res => console.log(res))
        .catch(error => console.log(error));



        setTimeout(()=>{

            saveOrder(true);

        },500)

        saveOrder(false);
    }

    const [orderPlaced, saveOrder] = useState(false);



    return ( 
        <div style={background}>
            {orderPlaced ? <Spinner /> :
            (
                <React.Fragment>
                    <h3 style={text}>Seleccioná los gustos!</h3>

                    <Form.Group>
                        <Form.Label style={labelPosition}>Elige el primer gusto!</Form.Label>
                        <Form.Control as="select" style={selectPosition}>
                            <option>Chocolate</option>
                            <option>Vainilla</option>
                            <option>Crema americana</option>
                            <option>Dulce de leche</option>
                        </Form.Control>
                        <br/>
                        <Form.Label style={labelPosition}>Elige el segundo gusto!</Form.Label>
                        <Form.Control as="select" style={selectPosition}>
                            <option>Chocolate</option>
                            <option>Vainilla</option>
                            <option>Crema americana</option>
                            <option>Dulce de leche</option>
                        </Form.Control>
                    </Form.Group>
                    <br/>
                    <button 
                        style={buttonStyle}
                        onClick={(e) => sendOrder(e)}
                    >Pedir!</button>
                </React.Fragment>
            )}
        </div>
     );
}

export default Cono;
