import React from 'react';
import {Card,Row,Col, Button} from 'react-bootstrap';

const CardProduct = ({name, image, description}) => {

    const imageSize = {
        width: "210px",
        height: "315px"
    }

    const color = {
        backgroundColor:"cornflowerblue"
    }
    return ( 
        <Card style={color}>
            <Row>
                <Col><Card.Img style={imageSize} variant="top" src={image} />
                </Col>
                <Col>
                    <Card.Body>
                        <Card.Title>{name}</Card.Title>
                        <Card.Text>
                            {description}
                        </Card.Text>
                        <Button variant="primary" href="/helados/cono">Ped√≠ tu helado</Button>
                    </Card.Body>
                </Col>
            </Row>
        </Card>
     );
}

export default CardProduct;
