import React from 'react';
import { Form } from 'react-bootstrap';


const Cono = () => {

    const background = {
        backgroundColor: "royalblue"
    }

    const text = {
        textAlign:"center",
        color:"snow"
    }

    const labelPosition = {
        color:"snow",
        marginLeft:"25%"
    }

    const selectPosition = {
        width:"50%",
        marginLeft:"25%"
    }

    const buttonStyle = {
        backgroundColor: "snow",
        border: "none",
        color: "royalblue",
        padding: "15px 32px",
        textAlign: "center",
        textDecoration: "none",
        display: "inline-block",
        fontSize: "16px",
        cursor: "pointer",
        marginLeft:"47%"
    }
    return ( 
        <div style={background}>
            <h3 style={text}>Seleccion√° los gustos!</h3>

            <Form.Group>
                <Form.Label style={labelPosition}>Elige el primer gusto!</Form.Label>
                <Form.Control as="select" style={selectPosition}>
                    <option>Chocolate</option>
                    <option>Vainilla</option>
                    <option>Crema americana</option>
                    <option>Dulce de leche</option>
                </Form.Control>
                <br/>
                <Form.Label style={labelPosition}>Elige el segundo gusto!</Form.Label>
                <Form.Control as="select" style={selectPosition}>
                    <option>Chocolate</option>
                    <option>Vainilla</option>
                    <option>Crema americana</option>
                    <option>Dulce de leche</option>
                </Form.Control>
            </Form.Group>

            <button style={buttonStyle}>Pedir!</button>
        </div>

     );
}

export default Cono;
