import React, {useState} from 'react';
import { Alert, Form } from 'react-bootstrap';
import axios from 'axios';
import Spinner from '../Spinner/Spinner';
import AlertMessage from '../AlertMessage';


const Cono = () => {

    const background = {
        backgroundColor: "royalblue"
    }

    const text = {
        textAlign:"center",
        color:"snow"
    }

    const labelPosition = {
        color:"snow",
        marginLeft:"25%"
    }

    const selectPosition = {
        width:"50%",
        marginLeft:"25%"
    }

    const buttonStyle = {
        backgroundColor: "snow",
        border: "none",
        color: "royalblue",
        padding: "15px 32px",
        textAlign: "center",
        textDecoration: "none",
        display: "inline-block",
        fontSize: "16px",
        cursor: "pointer",
        marginLeft:"47%",
        marginBottom:"10px"
    }

    const successBuyAlert = {
        width: "50%",
        left: "25%",
        textAlign: "center"
    }

    const sendOrder = (e) =>{
        e.preventDefault();

        const url = "http://localhost:8080/venta/crearVenta";

        axios.post(url,{
            tipoHelado:'Cono'
        })
        .then(res => {
            console.log(res.data);
            if(res.data === "No hay cajeros disponibles"){
                saveError(true);
            }
        })
        .catch(error => console.log(error));


        saveOrder(true);

        setTimeout(()=>{

            saveOrder(false);
            saveAlert(true);

        },2000);

        setTimeout(()=>{

            saveAlert(false);
            saveError(false);

        },5000);

    }

    const [orderPlaced, saveOrder] = useState(false);
    const [alertBuy, saveAlert] = useState(false)



    return ( 
        <div style={background}>
            {alertBuy? errorAlert?<AlertMessage message="No se pudo concretar la compra, no hay cajeros disponibles."/>: <AlertMessage message="Se concretó la compra"/> :null}
            {orderPlaced ? <Spinner /> :
            (
                <React.Fragment>
                    <h3 style={text}>Seleccioná los gustos!</h3>

                    <Form.Group>
                        <Form.Label style={labelPosition}>Elige el primer gusto!</Form.Label>
                        <Form.Control as="select" style={selectPosition}>
                            <option>Chocolate</option>
                            <option>Vainilla</option>
                            <option>Crema americana</option>
                            <option>Dulce de leche</option>
                        </Form.Control>
                        <br/>
                        <Form.Label style={labelPosition}>Elige el segundo gusto!</Form.Label>
                        <Form.Control as="select" style={selectPosition}>
                            <option>Chocolate</option>
                            <option>Vainilla</option>
                            <option>Crema americana</option>
                            <option>Dulce de leche</option>
                        </Form.Control>
                    </Form.Group>
                    <br/>
                    <button 
                        style={buttonStyle}
                        onClick={(e) => sendOrder(e)}
                    >Pedir!</button>
                    <br/>
                </React.Fragment>
            )}
        </div>
     );
}

export default Cono;
