import React from 'react';
import {Card,Row,Col, Button} from 'react-bootstrap';

const CardProduct = ({name, image, description}) => {

    const color = {
        backgroundImage:`url(${image})`,
        width:"100%",
        height:"300px",
        backgrundSize:"cover"
    }
    return ( 
        <Card style={color}>
            <Row>
                <Col>
                    <Card.Body>
                        <Card.Title>{name}</Card.Title>
                        <Card.Text>
                            {description}
                        </Card.Text>
                        <Button variant="primary" href="/helados/cono">Ped√≠ tu helado</Button>
                    </Card.Body>
                </Col>
            </Row>
        </Card>
     );
}

export default CardProduct;
